mapper.update:
UPDATE user
nickname = #{nickname}, social_id = #{socialId}, social_type = #{socialType}, fcm_token = #{fcmToken}, refresh_token = #{refreshToken}, last_login_at = #{lastLoginAt}, updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT *
FROM user
WHERE social_id = #{socialId}
  AND social_type = #{socialType}
